import React from "react"
import { ReactComponent as MyIcon } from '../../src/close-big.svg'; // Import SVG as a React component

const Square = ({id , onClick , state}) =>{
    const checkIcon = ()=>{
        if(state == 'x')
            return MyIcon

        else if (state === 'o')
            return 'o'

        else
            return null
    }
    return(
        <>
        <div className="square" id={id} onClick={() => {onClick(id)}}>
            {
                state === 'x' ? MyIcon : 'o'
            }
        </div>
        </>
    )
}
export default Square
