const Form = ({setNames})=>{
    const onSubmit = (e)=>{
        e.preventDefault()

    const player1 = document.querySelector('.player1')
    if(!player1.value){
        alert('player1 name is empty')
    }    

    const player2 = document.querySelector('.player2')
    if(!player2.value){
        alert('player2 name is empty')
    }

    setNames(player1.value , player2.value);

    }

    return (
        <div class="form" onSubmit={onSubmit}>
        <form class="container">
            <div class="item">
                <label>player1 name: </label>
                <input type="text" id="player1" placeholder="X"/>
            </div>

            <div class="item">
                <label >player2 name: </label>
                <input type="text" id="player2" placeholder="O"/>
            </div>

            <input type="submit" class="item"/>
        </form>
     </div>
    )
}

export default Form
