import './App.css';
import Board  from './components/board';
import { useState } from 'react';
function App() {

  const [player , setPlayer] = useState(0)
  const [boardState , setBoardState] = useState([null,null,null,null,null,null,null,null,null])

  const switchPlayer = ()=>{
    setPlayer(!player);
  }

  const onClick = (id)=>{
    if(boardState[id] == null){
      if(player)
        setBoardState(boardState.map((element , index)=>
          index === id ? 'o' : element
        ))


      else
        setBoardState(boardState.map((element , index)=>
          index === id ? 'x' : element
        ))
      checkGame(player);
      switchPlayer();
    }
  }  
  const checkGame=(player)=>{
    if(
      (boardState[0] === boardState[1] && boardState[1] === boardState[2] && boardState[0] != null) || 
      (boardState[3] === boardState[4] && boardState[4] === boardState[5] && boardState[3] != null) ||
      (boardState[6] === boardState[7] && boardState[7] === boardState[8] && boardState[6] != null)
    )
      console.log('player win' , player)

    else if(
      (boardState[0] === boardState[3] && boardState[3] === boardState[6]) || 
      (boardState[1] === boardState[4] && boardState[4] === boardState[7]) ||
      (boardState[2] === boardState[5] && boardState[5] === boardState[8])
    )
      console.log('player win' , player)


    else if(
      (boardState[0] === boardState[4] && boardState[4] === boardState[8] )||
      (boardState[2] === boardState[4] && boardState[4] === boardState[6]))
      console.log('player win' , player)

  }

  return (
    <div className="App">
      <Board onClick = {onClick} board={boardState}/>
    </div>
  );
}

export default App;
