import './App.css';
import Board  from './components/board';
import { useState } from 'react';
function App() {

  const [player , setPlayer] = useState(0)
  const [boardState , setBoardState] = useState([null,null,null,null,null,null,null,null,null])

  const switchPlayer = ()=>{
    setPlayer(!player);
  }

  const onClick =  (id)=>{
    if(boardState[id] == null){

         setBoardState(boardState.map((element , index)=>
          index === id ?'9' : element
        ))



      checkGame();
      switchPlayer();
    }
  }  
  const checkGame =()=>{
    console.log(boardState[0] , boardState[1] , boardState[2])
    if(
      (boardState[0] === boardState[1] && boardState[1] === boardState[2] && boardState[0] != null) || 
      (boardState[3] === boardState[4] && boardState[4] === boardState[5] && boardState[3] != null) ||
      (boardState[6] === boardState[7] && boardState[7] === boardState[8] && boardState[6] != null)){
      console.log('player win 1' , player)
    }

    else if(
      (boardState[0] === boardState[3] && boardState[3] === boardState[6] && boardState[0] != null) || 
      (boardState[1] === boardState[4] && boardState[4] === boardState[7] && boardState[1] != null) ||
      (boardState[2] === boardState[5] && boardState[5] === boardState[8] && boardState[2] != null)){
      console.log('player win 2' , player)
    }


    else if(
      (boardState[0] === boardState[4] && boardState[4] === boardState[8] && boardState[0] != null)||
      (boardState[2] === boardState[4] && boardState[4] === boardState[6] && boardState[2] != null)){
        console.log('player win 3' , player)
      }

  }

  return (
    <div className="App">
      <Board onClick = {onClick} board={boardState}/>
    </div>
  );
}

export default App;
